//         borderWidth: 1,
//         padding: 10,
//     },
// });

const styles = StyleSheet.create({
    input: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: '#FFBF00',
    },

    textInput: {
      padding: 15,
      maxWidth: 250,
      paddingHorizontal: 15,
      backgroundColor: '#FFBF00',
      borderRadius: 60,
      borderColor: '#C0C0C0',
      borderWidth: 1
    },

  });

export default Screen1;
