import { LOGOUT, RESTORE_USER, SIGNUP, LOGIN } from "../actions/UserActions";


export interface UserState {
    idToken: string | undefined;
    email: string | undefined;
    username: string | undefined;
}

const initialState: UserState = {
    idToken: undefined,
    email: undefined,
    username: undefined
};

export interface Action {
    type: string;
    payload: any;
}

export interface Action {
    type: string;
    payload: any;
}

const userReducer = (state: UserState = initialState, action: Action) => {
    switch (action.type) {
        case SIGNUP:
            return { ...state, idToken: action.payload.idToken, email: action.payload.email }
        case RESTORE_USER:
            return { ...state, idToken: action.payload.idToken, email: action.payload.email }
            case LOGIN:
                return { ...state, idToken: action.payload.idToken, email: action.payload.email }

         case LOGOUT:
                return { ...state, idToken: undefined, email: undefined }
        default:
            return state; //does not do anything yetâ€‹   
    }
};

export default userReducer;
